function res = Aufgabe02()    for l = 1:10        h = 1/(2^l);        a = 0;        b = 1;        f = @(x) sin(pi * x) .* exp(x);        g = @(x) x.^(1/3);                fprintf("Schrittweite h = %d:\n", h);        I_f_real = integral(f, a, b);        fprintf("f echtes Integral: %f\n", I_f_real);        I_f_trapez = quad(f, a, b, h, 1);        E_f_trapez = abs(I_f_trapez - I_f_real);        fprintf("f Trapez: %f, Fehler: %f\n", I_f_trapez, E_f_trapez);        I_f_simpson = quad(f, a, b, h, 2);        E_f_simpson = abs(I_f_simpson - I_f_real);        fprintf("f Simpson: %f, Fehler: %f\n", I_f_simpson, E_f_simpson);        I_f_gauss = quad(f, a, b, h, 3);        E_f_gauss = abs(I_f_gauss - I_f_real);        fprintf("f Gauss: %f, Fehler: %f\n", I_f_gauss, E_f_gauss);                I_g_real = integral(g, a, b);        fprintf("g echtes Integral: %f\n", I_g_real);        I_g_trapez = quad(g, a, b, h, 1);        E_g_trapez = abs(I_g_trapez - I_g_real);        fprintf("g Trapez: %f, Fehler: %f\n", I_g_trapez, E_g_trapez);        I_g_simpson = quad(g, a, b, h, 2);        E_g_simpson = abs(I_g_simpson - I_g_real);        fprintf("g Simpson: %f, Fehler: %f\n", I_g_simpson, E_g_simpson);        I_g_gauss = quad(g, a, b, h, 3);        E_g_gauss = abs(I_g_gauss - I_g_real);        fprintf("g Gauss: %f, Fehler: %f\n\n", I_g_gauss, E_g_gauss);    endendfunction y = quad(f, a, b, h, mode)    y = 0;    start = a;    ende = a + h;    while (ende < b)        y = y + quadratur(f, start, ende, mode);        start = start + h;        ende = ende + h;    endendfunction y = quadratur(f, a, b, mode)    if mode == 1        % Verwende Trapez-Regel        y = (b-a)/2 * (f(a) + f(b));    elseif mode == 2        % Verwende Simpson-Regel        y = (b-a)/6 * (f(a) + 4 * f((a+b)/2) + f(b));    elseif mode == 3        % Verwende 3-Punkt Gauss-Legendre-Quadratur        j = ((a+b)/2) - sqrt(3/5) * ((b-a)/2);        k = (a+b)/2;        l = ((a+b)/2) + sqrt(3/5) * ((b-a)/2);        y = ((b-a)/18) * (5*f(j) + 8*f(k) + 5*f(l));    else        error("Mögliche Werte für mode: 1 = trapez, 2 = simpson, 3 = gauss");    endend